INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (1, '江苏国家应用数学（东南大学）中心揭牌', '7月30日下午，江苏国家应用数学（东南大学）中心揭牌仪式在南京紫金山庄紫金会议中心举行。菲尔兹奖得主、清华大学讲席教授丘成桐，东南大学党委书记左惟，中国科学院院士杨乐，中国科学院院士、南方科技大学校长薛其坤，中国工程院院士黄卫，江苏省科技厅二级巡视员景茂，东南大学副校长吴刚，江苏大学副校长田立新以及南京市科技局、教育局等出席了仪式。', '发布会', NULL, '东大新闻网', '胡建强', '2022-08-31 11:01:57', '科技', 230, 55, 0, NULL);
INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (2, '2022百度世界大会：AI深耕 万物生长', '7月21日上午9时，由百度与央视新闻联合举办的2022百度世界大会将在线上召开。本次百度世界大会以“AI深耕，万物生长”为主题，探索AI走向工厂车间、田间地头的路径。\n  百度创始人、董事长兼CEO李彦宏及百度各业务领军人将出席大会，分享最前沿的AI技术与产品，并发布百度在智能云、自动驾驶、大模型等领域产品。', '百度世界大会', 'adasdma', '聚焦IT', '佚名', '2022-08-26 13:57:00', '科技', 13683, 243, 2, 1);
INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (3, '长三角碳中和产学研联盟成立', '8月17日上午，第四届长三角一体化发展高层论坛在上海举行。来自长三角三省一市的党政主要领导、研究机构和智库专家、企业代表共商一体化大计，共谋高质量发展。本次论坛上，东南大学等区域内的6所高校、4家科研院所以及13家企事业单位共同发起成立了长三角碳中和产学研联盟，旨在为全国双碳目标的实现提供长三角解决方案。东南大学副校长吴刚、碳中和科学技术研究院执行院长蔡亮代表学校参加了联盟成立仪式。', '发布会', NULL, '东大新闻网', '蔡亮', '2022-08-31 11:03:07', '科技', 1003, 11, 0, NULL);
INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (4, '东南大学做好2022级新生开学准备工作', '8月20日，东南大学2022级4157名本科新生，88名二学位学生和79名预科生即将走进校园。8月30日，7402名2022级研究生新生也将来到东大（其中硕士6436人，博士966人）。为了让2022级新同学顺利开启大学生活，迎接五彩斑斓的新学期，学校各部门积极做好准备工作，以饱满的热情迎接新同学。\n\n学校提前开通“一站式”迎新网站，为新生提供报到须知、医保注册、宿舍预定、户籍登记、财务缴费、绿色通道、网上请假、体检预约、报到行程登记等便捷服务。', '教育', NULL, '东大新闻网', '唐瑭 杨万里', '2022-08-31 11:04:33', '文学', 175, 15, 0, NULL);
INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (5, '东南大学迎来2022级本科新生', '8月20日，东南大学各校区彩旗飘飘、热闹非凡，来自祖国各地的四千余名2022级本科新生怀着喜悦激动的心情前来报到，正式开启崭新的大学生活。校党委书记左惟，校长黄如，副校长黄大卫、吴刚，校党委副书记邢纪红等先后到九龙湖校区、四牌楼校区了解迎新工作情况，看望新同学，向新生和家长表示祝贺和热烈欢迎，并向酷暑中的迎新工作人员表示慰问。', '教育', NULL, '东大新闻网', '唐瑭', '2022-08-31 11:06:37', '文学', 268, 36, 0, NULL);
INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (6, '东南大学召开2022年全面从严治党工作会议暨十五届党委第二轮校内巡视工作部署动员大会', '8月25日下午，2022年全面从严治党工作会议暨东南大学十五届党委第二轮校内巡视工作部署动员大会在九龙湖校区纪忠楼报告厅举行。会议以全面贯彻落实十九届六中全会精神、十九届中央纪委六次全会精神、教育部党组和全省教育系统关于全面从严治党工作的新任务新要求等为指导，总结回顾2021年工作，统筹谋划推进全面从严治党的重点任务。', '从严治党', NULL, '东大新闻网', '徐润', '2022-08-31 11:08:15', '文学', 827, 99, 0, NULL);
INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (49, 'apple秋季产品发布会', '苹果公司刚刚向媒体发出2022年秋季发布会邀请函，确定将于美国当地时间9月7日（北京时间9月8日凌晨一点）举行特别活动。此次仍然采用线上形式，预计iPhone 14系列、Apple Watch Series8等产品将会在此时间推出。\n在发布会正式到来之前，新浪数码给大家盘点一下本次秋季发布会可能有哪些新品。iPhone 14系列\n作为本次发布会的主角', '发布会', 'https://pic1.zhimg.com/v2-b897e28bea588f1a2e77fd20433412d4_b.png', '知乎', '新浪娱乐', '2022-08-29 01:04:20', '科技', 39, 52, 0, 0);
INSERT INTO `article`(`article_id`, `title`, `summary`, `key_word`, `image_url`, `source`, `author`, `create_time`, `article_type`, `click_num`, `praise_count`, `comment_num`, `sort`) VALUES (50, '让学生真心喜爱终身受益', '对北京工商大学经济学院学生卢思璇来说，习近平新时代中国特色社会主义思想概论课让自己印象深刻。“课程形式很新颖，由两位老师共同讲授。一名专家进行网络串讲，一名校内教师进行专题解读。”卢思璇说，“老师从我们熟悉的身边事讲起，很有意思，同时在分组讨论中引导我们认识和理解当代中国，很有意义。”', '教育', '', '人民网', '丁雅诵', '2022-08-29 09:03:10', '文学', 16, 63, 0, NULL);
